gs":[]},"sources":{"contracts/flashloan/base/FlashLoanReceiverBase.sol":{"content":"pragma solidity ^0.5.0;

import "../../openzeppelin-contracts/SafeMath.sol";
import "../../openzeppelin-contracts/IERC20.sol";
import "../../openzeppelin-contracts/SafeERC20.sol";

import "../interfaces/IFlashLoanReceiver.sol";
import "../../interfaces/ILendingPoolAddressesProvider.sol";
import "../../libraries/EthAddressLib.sol";

contract FlashLoanReceiverBase is IFlashLoanReceiver {

    using SafeERC20 for IERC20;
    using SafeMath for uint256;

    ILendingPoolAddressesProvider public addressesProvider;

    constructor(ILendingPoolAddressesProvider _provider) public {
        addressesProvider = _provider;
    }

    function () external payable {
    }

    function transferFundsBackToPoolInternal(address _reserve, uint256 _amount) internal {

        address payable core = addressesProvider.getLendingPoolCore();

        transferInternal(core,_reserve, _amount);
    }

    function transferInternal(address payable _destination, address _reserve, uint256  _amount) internal {
        if(_reserve == EthAddressLib.ethAddress()) {
            //solium-disable-next-line
            _destination.call.value(_amount)("");
            return;
        }

        IERC20(_reserve).safeTransfer(_destination, _amount);


    }

    function getBalanceInternal(address _target, address _reserve) internal view returns(uint256) {
        if(_reserve == EthAddressLib.ethAddress()) {

            return _target.balance;
        }

        return IERC20(_reserve).balanceOf(_target);

    }
}","keccak256":"0x9251a81785f5d180f81539ec6584048422ac35cadec3f549e3cb6ad4c1a795f8"},"contracts/flashloan/interfaces/IFlashLoanReceiver.sol":{"content":"pragma solidity ^0.5.0;

